sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var GlideRecordInsert = x_snc_devtools.GlideRecordInsert;
	var GlideRecordDelete = x_snc_devtools.GlideRecordDelete;
	var GetRecord = x_snc_devtools.GetRecord;
	var IsValidSysId = x_snc_devtools.IsValidSysId;
	
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);
	
	var strMessage = '_ATF_SELFCARE_\nThis is a test!';

	
	test.Log('Creating entry in syslog...');
	var grSysLog = new GlideRecord('syslog');
	grSysLog.initialize();
	grSysLog.source = 'Evaluator';
	grSysLog.message = strMessage;
	grSysLog.level = 'Warning';
	var strSysLogSysId = GlideRecordInsert(grSysLog);
	
	if (IsValidSysId(strSysLogSysId) == false)
	{
		test.Log('FAILED: Creating log entry failed.');
		test.SetResult(false);
		return test.GetResult();	
	}
	
	// Execute twice and check if the result is exactly one incident
	test.Log('Running ExecuteLogScan twice...');
	ExecuteLogScan();
	ExecuteLogScan();
	
	test.Log('Cleaning up the log entry (as it will not be cleaned up by ATF)');
	var grSysLog = GetRecord('syslog',strSysLogSysId);
	GlideRecordDelete(grSysLog);
	
	
	test.Log('Checking resulting incidents...');
	var grIncident = new GlideRecord('incident');
	grIncident.addQuery('description','CONTAINS',strMessage);
	grIncident.query();
	
	if (grIncident.getRowCount() != 1)
	{
		test.Log('FAILED: Did not find exactly ONE incident.');
		test.SetResult(false);
		return test.GetResult();	
		
	}
	
	test.Log('PASSED');
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
